import "./App.css";
import { Routes, Route, Link } from "react-router-dom";
import BlogsPage from "./Pages/Blogs";
import { useState, useEffect } from "react";
const urlEndpoint = "http://localhost:4000";

const App = () => {
  const [serverJSON, setServerJSON] = useState({ message: [] });
  const [sortField, setSortField] = usedState(null);
  const [sortOrder, setSortOrder] = usedState("ASC");
  const [filterField, setFilterField] = usedState(null);
  const [filterValue, setFilterValue] = usedState(null);
  const [limit, setLimit] = usedState(number(0));
  const [page, setPage] = usedState(0);

  useEffect(() => {
    const fetchData = async () => {
      const apiResponse = await fetch(`${urlEndpoint}/blogs/all-blogs`);
      const apiJSON = await apiResponse.json();
      setServerJSON(apiJSON);
      return;
    };
    fetchData();
  }, []);
  return (
    <div className="App">
      <Routes>
        <Route
          path="/blogs"
          element={<BlogsPage blogs={serverJSON.message} />}
        />
      </Routes>
    </div>
  );
};

export default App;
