  //     const apiResponse = await fetch(url);
  //     const apiJSON = await apiResponse.json();
  //     setServerJSON(apiJSON);
  //     return;
  //   };
  //   fetchData();
  // }, []);

  return (
    <div className="App">
      <Routes>
        <Route
          path="/blogs"
          element={<BlogsPage message={serverJSON.message} />}
        ></Route>
      </Routes>
    </div>
  );
}
export default App;
